// src/components/EmployeeList.js
import React, { useState, useEffect } from 'react';
import {
    Card,
    CardContent,
    Button,
    Typography,
    Grid,
    Dialog,
    DialogTitle,
    DialogContent,
    DialogActions,
    TextField,
} from '@mui/material';
import ApiService from '../services/ApiService';

const EmployeeList = ({ departmentId }) => {
    const [employees, setEmployees] = useState([]);
    const [selectedEmployee, setSelectedEmployee] = useState(null);
    const [deleteResult, setDeleteResult] = useState(null);
    const [isDialogOpen, setIsDialogOpen] = useState(false);

    useEffect(() => {
        ApiService.getEmployeesByDepartment(departmentId)
            .then(response => setEmployees(response.data))
            .catch(error => console.error('Error fetching employees:', error));
    }, [departmentId]);

    const handleEmployeeButtonClick = (employee) => {
        setSelectedEmployee(employee);
        setIsDialogOpen(true);
    };

    const handleDialogClose = () => {
        setIsDialogOpen(false);
        setDeleteResult(null);
        setSelectedEmployee(null);
    };

    const handleDeleteEmployee = () => {
        ApiService.deleteEmployee(selectedEmployee.employeeID)
            .then(() => {
                setEmployees(employees.filter(emp => emp.employeeID !== selectedEmployee.employeeID));
                setDeleteResult('Employee deleted successfully.');
            })
            .catch(error => {
                console.error('Error deleting employee:', error);
                setDeleteResult('Error deleting employee.');
            });
    };

    return (
        <Grid container spacing={2}>
            {employees.map(employee => (
                <Grid item xs={12} sm={6} md={4} lg={3} key={employee.employeeID}>
                    <Card>
                        <CardContent>
                            <Typography variant="h6" gutterBottom>
                                {employee.name}
                            </Typography>
                            <Button
                                variant="outlined"
                                color="secondary"
                                onClick={() => handleEmployeeButtonClick(employee)}
                            >
                                Delete Employee
                            </Button>
                        </CardContent>
                    </Card>
                </Grid>
            ))}

            <Dialog open={isDialogOpen} onClose={handleDialogClose}>
                <DialogTitle>Delete Employee</DialogTitle>
                <DialogContent>
                    <Typography>Are you sure you want to delete the employee?</Typography>
                    <Typography>{selectedEmployee?.name}</Typography>
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleDialogClose}>Cancel</Button>
                    <Button color="secondary" onClick={handleDeleteEmployee}>
                        Delete
                    </Button>
                </DialogActions>
            </Dialog>
        </Grid>
    );
};

export default EmployeeList;
