// src/components/EmployeeList.js
import React, { useState, useEffect } from 'react';
import ApiService from '../services/ApiService';
import { Button, Dialog, DialogTitle, DialogContent, DialogActions } from '@mui/material';

const EmployeeList = ({ departmentId }) => {
    const [employees, setEmployees] = useState([]);
    const [selectedEmployee, setSelectedEmployee] = useState(null);
    const [isDeleteDialogOpen, setDeleteDialogOpen] = useState(false);
    const [isDetailDialogOpen, setDetailDialogOpen] = useState(false);

    useEffect(() => {
        ApiService.getEmployeesByDepartment(departmentId)
            .then(response => setEmployees(response.data))
            .catch(error => console.error('Error fetching employees:', error));
    }, [departmentId]);

    const handleDeleteEmployee = (employeeId) => {
        setDeleteDialogOpen(true);
        setSelectedEmployee(employeeId);
    };

    const handleConfirmDelete = () => {
        ApiService.deleteEmployee(selectedEmployee)
            .then(() => {
                setEmployees(employees.filter(employee => employee.id !== selectedEmployee));
                setDeleteDialogOpen(false);
                setSelectedEmployee(null);
            })
            .catch(error => console.error('Error deleting employee:', error));
    };

    const handleShowDetails = (employeeId) => {
        setDetailDialogOpen(true);
        setSelectedEmployee(employeeId);
    };

    const handleCloseDialogs = () => {
        setDeleteDialogOpen(false);
        setDetailDialogOpen(false);
        setSelectedEmployee(null);
    };

    return (
        <div>
            <h2>Employee List for Department {departmentId}</h2>
            <ul>
                {employees.map(employee => (
                    <li key={employee.id}>
                        {employee.name}
                        <Button onClick={() => handleShowDetails(employee.id)}>Show Details</Button>
                        <Button onClick={() => handleDeleteEmployee(employee.id)}>Delete</Button>
                    </li>
                ))}
            </ul>

            {/* Delete Employee Dialog */}
            <Dialog open={isDeleteDialogOpen} onClose={handleCloseDialogs}>
                <DialogTitle>Delete Employee</DialogTitle>
                <DialogContent>
                    Are you sure you want to delete this employee?
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleCloseDialogs}>Cancel</Button>
                    <Button onClick={handleConfirmDelete} autoFocus>
                        Delete
                    </Button>
                </DialogActions>
            </Dialog>

            {/* Show Employee Details Dialog */}
            <Dialog open={isDetailDialogOpen} onClose={handleCloseDialogs}>
                <DialogTitle>Employee Details</DialogTitle>
                <DialogContent>
                    {/* Adaugă aici componentele sau informațiile necesare pentru afișarea detaliilor angajatului */}
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleCloseDialogs}>Close</Button>
                </DialogActions>
            </Dialog>
        </div>
    );
};

export default EmployeeList;
