// src/components/DepartmentList.js
import React, { useState, useEffect } from 'react';
import {
    Card,
    CardContent,
    Button,
    Typography,
    Grid,
    Dialog,
    DialogTitle,
    DialogContent,
    DialogActions,
    TextField,
    Select,
    MenuItem,
    FormControl,
    InputLabel,
} from '@mui/material';
import ApiService from '../services/ApiService';

const DepartmentList = () => {
    const [departments, setDepartments] = useState([]);
    const [selectedDepartment, setSelectedDepartment] = useState(null);
    const [deleteResult, setDeleteResult] = useState(null);
    const [isDialogOpen, setIsDialogOpen] = useState(false);
    const [isAddDialogOpen, setIsAddDialogOpen] = useState(false);
    const [newDepartmentData, setNewDepartmentData] = useState({
        description: '',
        parentID: null,
        managerID: null,
    });

    useEffect(() => {
        ApiService.getDepartments()
            .then(response => setDepartments(response.data))
            .catch(error => console.error('Error fetching departments:', error));
    }, []);

    const handleDepartmentButtonClick = (department) => {
        setSelectedDepartment(department);
        setIsDialogOpen(true);
    };

    const handleDialogClose = () => {
        setIsDialogOpen(false);
        setDeleteResult(null);
        setSelectedDepartment(null);
    };

    const handleDeleteDepartment = () => {
        ApiService.deleteDepartment(selectedDepartment.departmentID)
            .then(() => {
                setDepartments(departments.filter(dep => dep.departmentID !== selectedDepartment.departmentID));
                setDeleteResult('Department deleted successfully.');
            })
            .catch(error => {
                console.error('Error deleting department:', error);
                setDeleteResult('Error deleting department.');
            });
    };

    const handleAddDepartmentClick = () => {
        setIsAddDialogOpen(true);
    };

    const handleAddDialogClose = () => {
        setIsAddDialogOpen(false);
        setNewDepartmentData({
            description: '',
            parentID: null,
            managerID: null,
        });
    };

    const handleAddDepartment = () => {
        ApiService.addDepartment(newDepartmentData)
            .then(response => {
                setDepartments([...departments, response.data]);
                setIsAddDialogOpen(false);
                setNewDepartmentData({
                    description: '',
                    parentID: null,
                    managerID: null,
                });
            })
            .catch(error => console.error('Error adding department:', error));
    };

    return (
        <Grid container spacing={2}>
            <Grid item xs={12}>
                <Button variant="contained" color="primary" onClick={handleAddDepartmentClick}>
                    Add Department
                </Button>
            </Grid>
            {departments.map(department => (
                <Grid item xs={12} sm={6} md={4} lg={3} key={department.departmentID}>
                    <Card>
                        <CardContent>
                            <Typography variant="h6" gutterBottom>
                                {department.description}
                            </Typography>
                            <Button
                                variant="outlined"
                                color="secondary"
                                onClick={() => handleDepartmentButtonClick(department)}
                            >
                                Delete Department
                            </Button>
                        </CardContent>
                    </Card>
                </Grid>
            ))}

            <Dialog open={isDialogOpen} onClose={handleDialogClose}>
                <DialogTitle>Delete Department</DialogTitle>
                <DialogContent>
                    {deleteResult ? (
                        <Typography>{deleteResult}</Typography>
                    ) : (
                        <Typography>{`Are you sure you want to delete the department "${selectedDepartment?.description || ''}"?`}</Typography>
                    )}
                </DialogContent>
                <DialogActions>
                    {!deleteResult && (
                        <>
                            <Button onClick={handleDialogClose}>Cancel</Button>
                            <Button onClick={handleDeleteDepartment} color="secondary">
                                Delete
                            </Button>
                        </>
                    )}
                </DialogActions>
            </Dialog>

            <Dialog open={isAddDialogOpen} onClose={handleAddDialogClose}>
                <DialogTitle>Add Department</DialogTitle>
                <DialogContent>
                    <TextField
                        label="Description"
                        value={newDepartmentData.description}
                        onChange={(e) => setNewDepartmentData({ ...newDepartmentData, description: e.target.value })}
                        fullWidth
                        margin="normal"
                    />
                    <FormControl fullWidth margin="normal">
                        <InputLabel>Parent Department</InputLabel>
                        <Select
                            value={newDepartmentData.parentID || ''}
                            onChange={(e) => setNewDepartmentData({ ...newDepartmentData, parentID: e.target.value })}
                        >
                            <MenuItem value={null}>None</MenuItem>
                            {departments.map(dep => (
                                <MenuItem key={dep.departmentID} value={dep.departmentID}>
                                    {dep.description}
                                </MenuItem>
                            ))}
                        </Select>
                    </FormControl>
                    <FormControl fullWidth margin="normal">
                        <InputLabel>Manager</InputLabel>
                        <Select
                            value={newDepartmentData.managerID || ''}
                            onChange={(e) => setNewDepartmentData({ ...newDepartmentData, managerID: e.target.value })}
                        >
                            <MenuItem value={null}>None</MenuItem>
                            {/* Poți adăuga opțiuni pentru manageri aici */}
                        </Select>
                    </FormControl>
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleAddDialogClose}>Cancel</Button>
                    <Button onClick={handleAddDepartment} color="primary">
                        Add
                    </Button>
                </DialogActions>
            </Dialog>
        </Grid>
    );
};

export default DepartmentList;
