import axios from 'axios';

const ApiService = {
    getDepartments: () => axios.get(`http://localhost:8080/api/department/getAll`),
    getEmployeesByDepartment: (departmentId) => axios.get(`http://localhost:8080/api/employee/getAllEmployeesPerDepartment/${departmentId}`),
    getManagersByDepartment: (departmentId) => axios.get(`http://localhost:8080/api/employee/getAllManagersPerDepartment/${departmentId}`),
    sendEmail: (selectedEmployees, emailData) => axios.post(`http://localhost:8080/api/email/send`, { selectedEmployees, emailData }),
    deleteDepartment: (departmentId) => axios.delete(`http://localhost:8080/api/department/delete/${departmentId}`),
    addDepartment: (departmentData) => axios.post('http://localhost:8080/api/department/create', departmentData),
};

export default ApiService;
