// src/components/DepartmentList.js
import React, { useState, useEffect } from 'react';
import { Card, CardContent, Button, Typography, Grid } from '@mui/material';
import ApiService from '../services/ApiService';

const DepartmentList = () => {
    const [departments, setDepartments] = useState([]);

    useEffect(() => {
        ApiService.getDepartments()
            .then(response => setDepartments(response.data))
            .catch(error => console.error('Error fetching departments:', error));
    }, []);

    const handleDepartmentButtonClick = (departmentID) => {
        // Add any desired actions for the department button click
        console.log(`Clicked on department ${departmentID}`);
    };

    return (
        <Grid container spacing={2}>
            {departments.map(department => (
                <Grid item xs={12} sm={6} md={4} lg={3} key={department.departmentID}>
                    <Card>
                        <CardContent>
                            <Typography variant="h6" gutterBottom>
                                {department.description}
                            </Typography>
                            <Button
                                variant="outlined"
                                color="primary"
                                onClick={() => handleDepartmentButtonClick(department.departmentID)}
                            >
                                View Details
                            </Button>
                        </CardContent>
                    </Card>
                </Grid>
            ))}
        </Grid>
    );
};

export default DepartmentList;
