// src/components/EmployeeList.js
import React, { useState, useEffect } from 'react';
import { Card, CardContent, Typography, Grid, Button, Dialog, DialogTitle, DialogContent, DialogActions, TextField } from '@mui/material';
import ApiService from '../services/ApiService';

const EmployeeList = () => {
    const [employees, setEmployees] = useState([]);
    const [openAddEmployeeDialog, setOpenAddEmployeeDialog] = useState(false);
    const [newEmployee, setNewEmployee] = useState({ id: '', name: '', managerID: '', email: '' });
    const [selectedEmployee, setSelectedEmployee] = useState(null);
    const [deleteResult, setDeleteResult] = useState(null);
    const [isInfoDialogOpen, setIsInfoDialogOpen] = useState(false);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await ApiService.getEmployees();
                setEmployees(response.data);
            } catch (error) {
                console.error('Error fetching employees:', error);
            }
        };

        fetchData();
    }, []);

    const handleAddEmployeeClick = () => {
        setOpenAddEmployeeDialog(true);
    };

    const handleDialogClose = () => {
        setOpenAddEmployeeDialog(false);
        setDeleteResult(null);
        setSelectedEmployee(null);
    };

    const handleDeleteEmployee = async () => {
        try {
            await ApiService.deleteEmployee(selectedEmployee.id);
            // Re-fetch employees after deleting one
            const response = await ApiService.getEmployees();
            setEmployees(response.data);
            setDeleteResult('Employee deleted successfully.');
        } catch (error) {
            console.error('Error deleting employee:', error);
            setDeleteResult('Error deleting employee.');
        }
    };

    const handleShowInfoClick = (employee) => {
        setSelectedEmployee(employee);
        setIsInfoDialogOpen(true);
    };

    const handleInfoDialogClose = () => {
        setIsInfoDialogOpen(false);
        setSelectedEmployee(null);
    };

    return (
        <div>
            <Grid container spacing={2}>
                {employees.map(employee => (
                    <Grid item xs={12} sm={6} md={4} lg={3} key={employee.id}>
                        <Card>
                            <CardContent>
                                <Typography variant="h6" gutterBottom>
                                    {employee.name}
                                </Typography>
                                <Button
                                    variant="outlined"
                                    color="secondary"
                                    onClick={() => handleDeleteEmployeeClick(employee)}
                                >
                                    Delete Employee
                                </Button>
                                <Button
                                    variant="outlined"
                                    color="primary"
                                    onClick={() => handleShowInfoClick(employee)}
                                >
                                    Show All Info
                                </Button>
                            </CardContent>
                        </Card>
                    </Grid>
                ))}
            </Grid>
            <Button variant="contained" color="primary" onClick={handleAddEmployeeClick}>
                Add Employee
            </Button>
            <Dialog open={openAddEmployeeDialog} onClose={handleDialogClose}>
                {/* Existing code for the add employee dialog */}
            </Dialog>
            <Dialog open={isInfoDialogOpen} onClose={handleInfoDialogClose}>
                {/* Existing code for the employee info dialog */}
            </Dialog>
        </div>
    );
};

export default EmployeeList;
