// src/components/EmployeeList.js
import React, { useState, useEffect } from 'react';
import { Card, CardContent, Typography, Grid, Button, Dialog, DialogTitle, DialogContent, DialogActions, TextField } from '@mui/material';
import ApiService from '../services/ApiService';

const EmployeeList = () => {
    const [employees, setEmployees] = useState([]);
    const [isAddDialogOpen, setIsAddDialogOpen] = useState(false);
    const [newEmployeeData, setNewEmployeeData] = useState({
        name: '',
        departmentId: '',
        email: '',
        managerId: '',
    });

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await ApiService.getEmployees();
                setEmployees(response.data);
            } catch (error) {
                console.error('Error fetching employees:', error);
            }
        };

        fetchData();
    }, []);

    const handleAddDialogClick = () => {
        setIsAddDialogOpen(true);
    };

    const handleAddDialogClose = () => {
        setIsAddDialogOpen(false);
        setNewEmployeeData({
            name: '',
            departmentId: '',
            email: '',
            managerId: '',
        });
    };

    const handleAddEmployee = () => {
        ApiService.addEmployee(newEmployeeData)
            .then(response => {
                setEmployees([...employees, response.data]);
                setIsAddDialogOpen(false);
                setNewEmployeeData({
                    name: '',
                    departmentId: '',
                    email: '',
                    managerId: '',
                });
            })
            .catch(error => console.error('Error adding employee:', error));
    };

    return (
        <div>
            <Button variant="contained" color="primary" onClick={handleAddDialogClick}>
                Add Employee
            </Button>

            <Grid container spacing={2}>
                {employees.map(employee => (
                    <Grid item xs={12} sm={6} md={4} lg={3} key={employee.id}>
                        <Card>
                            <CardContent>
                                <Typography variant="h6" gutterBottom>
                                    {employee.name}
                                </Typography>
                            </CardContent>
                        </Card>
                    </Grid>
                ))}
            </Grid>

            <Dialog open={isAddDialogOpen} onClose={handleAddDialogClose}>
                <DialogTitle>Add Employee</DialogTitle>
                <DialogContent>
                    <TextField
                        label="Name"
                        fullWidth
                        value={newEmployeeData.name}
                        onChange={(e) => setNewEmployeeData({ ...newEmployeeData, name: e.target.value })}
                    />
                    <TextField
                        label="Department ID"
                        fullWidth
                        value={newEmployeeData.departmentId}
                        onChange={(e) => setNewEmployeeData({ ...newEmployeeData, departmentId: e.target.value })}
                    />
                    <TextField
                        label="Email"
                        fullWidth
                        value={newEmployeeData.email}
                        onChange={(e) => setNewEmployeeData({ ...newEmployeeData, email: e.target.value })}
                    />
                    <TextField
                        label="Manager ID"
                        fullWidth
                        value={newEmployeeData.managerId}
                        onChange={(e) => setNewEmployeeData({ ...newEmployeeData, managerId: e.target.value })}
                    />
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleAddDialogClose}>Cancel</Button>
                    <Button color="primary" onClick={handleAddEmployee}>
                        Add
                    </Button>
                </DialogActions>
            </Dialog>
        </div>
    );
};

export default EmployeeList;
