// src/components/EmailForm.js
import React, { useState } from 'react';
import {
    Button,
    Dialog,
    DialogTitle,
    DialogContent,
    DialogActions,
    TextField,
    Snackbar,
} from '@mui/material';
import MuiAlert from '@mui/material/Alert';
import ApiService from '../services/ApiService';

const EmailForm = ({ selectedEmployees }) => {
    const [open, setOpen] = useState(false);
    const [subject, setSubject] = useState('');
    const [body, setBody] = useState('');
    const [snackbarOpen, setSnackbarOpen] = useState(false);
    const [snackbarMessage, setSnackbarMessage] = useState('');
    const [snackbarSeverity, setSnackbarSeverity] = useState('success');

    const handleOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };

    const handleSendEmail = () => {
        const emailData = { subject, body };

        ApiService.sendEmail(selectedEmployees, emailData)
            .then(() => {
                setSnackbarMessage('Email sent successfully!');
                setSnackbarSeverity('success');
                setSnackbarOpen(true);
                handleClose();
            })
            .catch(error => {
                setSnackbarMessage(`Error sending email: ${error.message}`);
                setSnackbarSeverity('error');
                setSnackbarOpen(true);
            });
    };

    const handleSnackbarClose = (event, reason) => {
        if (reason === 'clickaway') {
            return;
        }
        setSnackbarOpen(false);
    };

    return (
        <div>
            <Button variant="contained" color="primary" onClick={handleOpen}>
                Open Email Form
            </Button>

            <Dialog open={open} onClose={handleClose}>
                <DialogTitle>Email Form</DialogTitle>
                <DialogContent>
                    <TextField
                        label="Subject"
                        value={subject}
                        onChange={e => setSubject(e.target.value)}
                        fullWidth
                        margin="normal"
                    />
                    <TextField
                        label="Body"
                        value={body}
                        onChange={e => setBody(e.target.value)}
                        fullWidth
                        multiline
                        rows={4}
                        margin="normal"
                    />
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleClose}>Cancel</Button>
                    <Button onClick={handleSendEmail} variant="contained" color="primary">
                        Send Email
                    </Button>
                </DialogActions>
            </Dialog>

            <Snackbar
                open={snackbarOpen}
                autoHideDuration={6000}
                onClose={handleSnackbarClose}
            >
                <MuiAlert
                    elevation={6}
                    variant="filled"
                    onClose={handleSnackbarClose}
                    severity={snackbarSeverity}
                >
                    {snackbarMessage}
                </MuiAlert>
            </Snackbar>
        </div>
    );
};

export default EmailForm;
