// src/components/EmployeeList.js
import React, { useState, useEffect } from 'react';
import {
    Card,
    CardContent,
    Button,
    Typography,
    Grid,
    Dialog,
    DialogTitle,
    DialogContent,
    DialogActions,
    TextField,
} from '@mui/material';
import ApiService from '../services/ApiService';

const EmployeeList = ({ departmentId }) => {
    const [employees, setEmployees] = useState([]);
    const [selectedEmployee, setSelectedEmployee] = useState(null);
    const [deleteResult, setDeleteResult] = useState(null);
    const [isDialogOpen, setIsDialogOpen] = useState(false);
    const [isAddDialogOpen, setIsAddDialogOpen] = useState(false);
    const [newEmployeeData, setNewEmployeeData] = useState({
        name: '',
        email: '',
    });

    useEffect(() => {
        ApiService.getEmployeesByDepartment(departmentId)
            .then(response => setEmployees(response.data))
            .catch(error => console.error('Error fetching employees:', error));
    }, [departmentId]);

    const handleEmployeeButtonClick = (employee) => {
        setSelectedEmployee(employee);
        setIsDialogOpen(true);
    };

    const handleDialogClose = () => {
        setIsDialogOpen(false);
        setDeleteResult(null);
        setSelectedEmployee(null);
    };

    const handleDeleteEmployee = () => {
        ApiService.deleteEmployee(selectedEmployee.id)
            .then(() => {
                setEmployees(employees.filter(emp => emp.id !== selectedEmployee.id));
                setDeleteResult('Employee deleted successfully.');
            })
            .catch(error => {
                console.error('Error deleting employee:', error);
                setDeleteResult('Error deleting employee.');
            });
    };

    const handleAddDialogClick = () => {
        setIsAddDialogOpen(true);
    };

    const handleAddDialogClose = () => {
        setIsAddDialogOpen(false);
        setNewEmployeeData({
            name: '',
            email: '',
        });
    };

    const handleAddEmployee = () => {
        ApiService.addEmployee(departmentId, newEmployeeData)
            .then(response => {
                setEmployees([...employees, response.data]);
                setIsAddDialogOpen(false);
                setNewEmployeeData({
                    name: '',
                    email: '',
                });
            })
            .catch(error => console.error('Error adding employee:', error));
    };

    return (
        <Grid container spacing={2}>
            <Grid item xs={12}>
                <Button variant="contained" color="primary" onClick={handleAddDialogClick}>
                    Add Employee
                </Button>
            </Grid>
            {employees.map(employee => (
                <Grid item xs={12} sm={6} md={4} lg={3} key={employee.id}>
                    <Card>
                        <CardContent>
                            <Typography variant="h6" gutterBottom>
                                {employee.name}
                            </Typography>
                            <Button
                                variant="outlined"
                                color="secondary"
                                onClick={() => handleEmployeeButtonClick(employee)}
                            >
                                Delete Employee
                            </Button>
                        </CardContent>
                    </Card>
                </Grid>
            ))}

            <Dialog open={isDialogOpen} onClose={handleDialogClose}>
                {/* Cod existent pentru dialogul de ștergere */}
                <DialogTitle>Delete Employee</DialogTitle>
                <DialogContent>
                    <Typography>Are you sure you want to delete the employee?</Typography>
                    <Typography>{selectedEmployee?.name}</Typography>
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleDialogClose}>Cancel</Button>
                    <Button color="secondary" onClick={handleDeleteEmployee}>
                        Delete
                    </Button>
                </DialogActions>
            </Dialog>

            <Dialog open={isAddDialogOpen} onClose={handleAddDialogClose}>
                {/* Cod existent pentru dialogul de adăugare */}
                <DialogTitle>Add Employee</DialogTitle>
                <DialogContent>
                    <TextField
                        label="Name"
                        fullWidth
                        value={newEmployeeData.name}
                        onChange={(e) => setNewEmployeeData({ ...newEmployeeData, name: e.target.value })}
                    />
                    <TextField
                        label="Email"
                        fullWidth
                        value={newEmployeeData.email}
                        onChange={(e) => setNewEmployeeData({ ...newEmployeeData, email: e.target.value })}
                    />
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleAddDialogClose}>Cancel</Button>
                    <Button color="primary" onClick={handleAddEmployee}>
                        Add
                    </Button>
                </DialogActions>
            </Dialog>
        </Grid>
    );
};

export default EmployeeList;
