// src/App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';
import DepartmentList from './components/DepartmentList';
import EmployeeList from './components/EmployeeList';
import EmailForm from './components/EmailForm';

const App = () => {
  const [selectedEmployees, setSelectedEmployees] = useState([]);

  const handleEmployeeSelect = (employeeId) => {
    // Implement employee selection logic here
  };

  return (
      <Router>
        <div>
          <nav>
            <ul>
              <li>
                <Link to="/">Home</Link>
              </li>
              <li>
                <Link to="/departments">Departments</Link>
              </li>
            </ul>
          </nav>

          <hr />

          <Route path="/" exact render={() => <h1>Director's App</h1>} />
          <Route path="/departments" exact component={DepartmentList} />
          <Route
              path="/departments/:id"
              render={({ match }) => (
                  <div>
                    <Link to="/departments">Back to Departments</Link>
                    <EmployeeList departmentId={match.params.id} />
                  </div>
              )}
          />
          <Route
              path="/send-email"
              render={() => (
                  <EmailForm selectedEmployees={selectedEmployees} />
              )}
          />
        </div>
      </Router>
  );
};

export default App;
