// src/App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import DepartmentList from './components/DepartmentList';
import EmployeeList from './components/EmployeeList';
import EmailForm from './components/EmailForm';
import Login from './components/Login';
import './App.css'; // Importul pentru stiluri

const App = () => {
    const [loggedInUser, setLoggedInUser] = useState(null);

    const handleLogin = (username) => {
        // You can perform authentication here, and set the logged-in user
        setLoggedInUser(username);
    };

    const handleLogout = () => {
        // Perform logout logic, e.g., clear user from state
        setLoggedInUser(null);
    };

    return (
        <Router>
            <div className="app-container">
                <header>
                    <h1>Director's App</h1>
                </header>
                <nav>
                    {loggedInUser ? (
                        // Render the authenticated navigation
                        <ul>
                            <li>
                                <Link to="/">Home</Link>
                            </li>
                            <li>
                                <Link to="/departments">Departments</Link>
                            </li>
                            <li>
                                <Link to="/employees">Employees</Link>
                            </li>
                            <li>
                                <button onClick={handleLogout}>Logout</button>
                            </li>
                        </ul>
                    ) : (
                        // Render the login component if not authenticated
                        <Login onLogin={handleLogin} />
                    )}
                </nav>
                <hr />
                <div className="content">
                    <Routes>
                        <Route path="/" element={<Home />} />
                        <Route path="/departments" element={<DepartmentList />} />
                        <Route path="/departments/:id" element={<DepartmentDetail />} />
                        <Route path="/employees" element={<EmployeeList />} />
                        <Route path="/employees/:departmentId" element={<EmployeeList />} />
                        <Route path="/send-email" element={<EmailForm />} />
                    </Routes>
                </div>
            </div>
        </Router>
    );
};

const Home = () => (
    <div>
        <h2>Welcome to the Director's App!</h2>
    </div>
);

const DepartmentDetail = ({ params }) => (
    <div>
        <Link to="/departments">Back to Departments</Link>
        <EmployeeList departmentId={params.id} />
    </div>
);

export default App;
