// src/components/DepartmentList.js
import React, { useState, useEffect } from 'react';
import {
    Card,
    CardContent,
    Button,
    Typography,
    Grid,
    Dialog,
    DialogTitle,
    DialogContent,
    DialogActions,
    List,
    ListItem,
} from '@mui/material';
import ApiService from '../services/ApiService';

const DepartmentList = () => {
    const [departments, setDepartments] = useState([]);
    const [selectedDepartment, setSelectedDepartment] = useState(null);
    const [employeeList, setEmployeeList] = useState([]);
    const [isDialogOpen, setIsDialogOpen] = useState(false);

    useEffect(() => {
        ApiService.getDepartments()
            .then(response => setDepartments(response.data))
            .catch(error => console.error('Error fetching departments:', error));
    }, []);

    const handleDepartmentButtonClick = (department) => {
        setSelectedDepartment(department);
        ApiService.getEmployeesByDepartment(department.departmentID)
            .then(response => setEmployeeList(response.data)) // Verifică numele proprietăților din obiectul employee
            .catch(error => console.error('Error fetching employees:', error));
        setIsDialogOpen(true);
    };

    const handleDialogClose = () => {
        setIsDialogOpen(false);
        setEmployeeList([]);
    };

    return (
        <Grid container spacing={2}>
            {departments.map(department => (
                <Grid item xs={12} sm={6} md={4} lg={3} key={department.departmentID}>
                    <Card>
                        <CardContent>
                            <Typography variant="h6" gutterBottom>
                                {department.description}
                            </Typography>
                            <Button
                                variant="outlined"
                                color="primary"
                                onClick={() => handleDepartmentButtonClick(department)}
                            >
                                View Details
                            </Button>
                        </CardContent>
                    </Card>
                </Grid>
            ))}

            <Dialog open={isDialogOpen} onClose={handleDialogClose}>
                <DialogTitle>{`Employees in ${selectedDepartment?.description || ''}`}</DialogTitle>
                <DialogContent>
                    <List>
                        {employeeList.map(employee => (
                            <ListItem key={employee.id}>
                                {employee.name} {/* Verifică numele proprietăților din obiectul employee */}
                            </ListItem>
                        ))}
                    </List>
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleDialogClose}>Close</Button>
                </DialogActions>
            </Dialog>
        </Grid>
    );
};

export default DepartmentList;
