// src/services/ApiService.js
import axios from 'axios';

const API_BASE_URL = 'http://localhost:8080/api';

const ApiService = {
    getDepartments: () => axios.get(`${API_BASE_URL}/department/getAll`),
    getEmployeesByDepartment: (departmentId) => axios.get(`${API_BASE_URL}/employee/getAllEmployeesPerDepartment/${departmentId}`),
    getManagersByDepartment: (departmentId) => axios.get(`${API_BASE_URL}/employee/getAllManagersPerDepartment/${departmentId}`),
    sendEmail: (selectedEmployees, emailData) => axios.post(`${API_BASE_URL}/email/send`, { selectedEmployees, emailData }),
};

export default ApiService;
