// src/components/EmployeeList.js
import React, { useState, useEffect } from 'react';
import { Card, CardContent, Typography, Grid, Button, TextField } from '@mui/material';
import ApiService from '../services/ApiService';

const EmployeeList = () => {
    const [employees, setEmployees] = useState([]);
    const [newEmployee, setNewEmployee] = useState({
        name: '',
        departmentId: '',
        email: '',
        managerId: '',
    });

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await ApiService.getEmployees();
                setEmployees(response.data);
            } catch (error) {
                console.error('Error fetching employees:', error);
            }
        };

        fetchData();
    }, []);

    const handleAddEmployee = async () => {
        try {
            await ApiService.addEmployee(newEmployee);
            // Reîmprospătează lista de angajați după adăugarea unui nou angajat
            const response = await ApiService.getEmployees();
            setEmployees(response.data);
            // Resetează formularul după adăugare
            setNewEmployee({
                name: '',
                departmentId: '',
                email: '',
                managerId: '',
            });
        } catch (error) {
            console.error('Error adding employee:', error);
        }
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewEmployee((prevEmployee) => ({
            ...prevEmployee,
            [name]: value,
        }));
    };

    return (
        <Grid container spacing={2}>
            <Grid item xs={12}>
                <Card>
                    <CardContent>
                        <Typography variant="h5" gutterBottom>
                            Add Employee
                        </Typography>
                        <form>
                            <TextField
                                label="Name"
                                name="name"
                                value={newEmployee.name}
                                onChange={handleInputChange}
                                fullWidth
                                margin="normal"
                            />
                            <TextField
                                label="Department ID"
                                name="departmentId"
                                value={newEmployee.departmentId}
                                onChange={handleInputChange}
                                fullWidth
                                margin="normal"
                            />
                            <TextField
                                label="Email"
                                name="email"
                                value={newEmployee.email}
                                onChange={handleInputChange}
                                fullWidth
                                margin="normal"
                            />
                            <TextField
                                label="Manager ID"
                                name="managerId"
                                value={newEmployee.managerId}
                                onChange={handleInputChange}
                                fullWidth
                                margin="normal"
                            />
                            <Button variant="contained" color="primary" onClick={handleAddEmployee}>
                                Add Employee
                            </Button>
                        </form>
                    </CardContent>
                </Card>
            </Grid>

            {employees.map((employee) => (
                <Grid item xs={12} sm={6} md={4} lg={3} key={employee.id}>
                    <Card>
                        <CardContent>
                            <Typography variant="h6" gutterBottom>
                                {employee.name}
                            </Typography>
                        </CardContent>
                    </Card>
                </Grid>
            ))}
        </Grid>
    );
};

export default EmployeeList;
