// src/components/EmployeeList.js
import React, { useState, useEffect } from 'react';
import { Card, CardContent, Typography, Grid, Button, Dialog, DialogTitle, DialogContent, TextField } from '@mui/material';
import ApiService from '../services/ApiService';

const EmployeeList = () => {
    const [employees, setEmployees] = useState([]);
    const [openAddEmployeeDialog, setOpenAddEmployeeDialog] = useState(false);
    const [newEmployee, setNewEmployee] = useState({ id: '', name: '', managerID: '', email: '' });

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await ApiService.getEmployees();
                setEmployees(response.data);
            } catch (error) {
                console.error('Error fetching employees:', error);
            }
        };

        fetchData();
    }, []);

    const handleAddEmployeeClick = () => {
        setOpenAddEmployeeDialog(true);
    };

    const handleDialogClose = () => {
        setOpenAddEmployeeDialog(false);
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewEmployee({ ...newEmployee, [name]: value });
    };

    const handleAddEmployee = async () => {
        try {
            await ApiService.addEmployee(newEmployee);
            // Re-fetch employees after adding a new one
            const response = await ApiService.getEmployees();
            setEmployees(response.data);
            setOpenAddEmployeeDialog(false);
            // Reset the newEmployee state
            setNewEmployee({ id: '', name: '', managerID: '', email: '' });
        } catch (error) {
            console.error('Error adding employee:', error);
        }
    };

    return (
        <div>
            <Grid container spacing={2}>
                {employees.map(employee => (
                    <Grid item xs={12} sm={6} md={4} lg={3} key={employee.id}>
                        <Card>
                            <CardContent>
                                <Typography variant="h6" gutterBottom>
                                    {employee.name}
                                </Typography>
                            </CardContent>
                        </Card>
                    </Grid>
                ))}
            </Grid>


            <Button variant="contained" color="primary" onClick={handleAddEmployeeClick}>
                Add Employee
            </Button>
            <Dialog open={openAddEmployeeDialog} onClose={handleDialogClose}>
                <DialogTitle>Add Employee</DialogTitle>
                <DialogContent>
                    <TextField
                        label="ID"
                        name="id"
                        value={newEmployee.id}
                        onChange={handleInputChange}
                        fullWidth
                        margin="normal"
                    />
                    <TextField
                        label="Name"
                        name="name"
                        value={newEmployee.name}
                        onChange={handleInputChange}
                        fullWidth
                        margin="normal"
                    />
                    <TextField
                        label="Manager ID"
                        name="managerID"
                        value={newEmployee.managerID}
                        onChange={handleInputChange}
                        fullWidth
                        margin="normal"
                    />
                    <TextField
                        label="Email"
                        name="email"
                        value={newEmployee.email}
                        onChange={handleInputChange}
                        fullWidth
                        margin="normal"
                    />
                    <Button variant="contained" color="primary" onClick={handleAddEmployee}>
                        Add
                    </Button>
                </DialogContent>
            </Dialog>
        </div>
    );
};

export default EmployeeList;
